import React, { useEffect, useRef, useState } from "react";
import Layout from "../components/layout";

const options = {
  weekday: "long",
  year: "numeric",
  month: "long",
  day: "numeric",
  hour: "2-digit",
  minute: "2-digit",
  second: "2-digit",
};

const formatAmount = (amount) =>
  `${amount}`.split(/(?=(?:\d{3})+(?:\.|$))/g).join(" ");
const formatDate = (date) =>
  `${new Date(date).toLocaleDateString("fr-FR", options)}`;

function HomePage() {
  const [kissKissAmount, setKissKissAmount] = useState(0);
  const [ululeAmount, setUluleAmount] = useState(0);
  const kissKissUpdate = useRef(null);
  const ululeUpdate = useRef(null);
  useEffect(() => {
    fetch("/api")
      .then((response) => response.json())
      .then(({ kisskissbankbank, ulule }) => {
        setKissKissAmount(formatAmount(kisskissbankbank));
        setUluleAmount(formatAmount(ulule));
      });
  }, []);
  return (
    <Layout>
      <h1>Projets populaires</h1>
      <h2>Argent collecté en temps réel</h2>
      <ul>
        <li>
          KissKissBankBank : <span>{kissKissAmount}</span>€ -
          <span ref={kissKissUpdate} />
        </li>
        <li>
          Ululee : <span>{ululeAmount}</span>€ -
          <span ref={ululeUpdate} />
        </li>
      </ul>
    </Layout>
  );
}

export default HomePage;
